System.out.println("Provisioning plan placeholder created for identity:"+EmailAddress);


List acctReqs = new ArrayList();
AccountRequest EntraGuestAcctReq = new AccountRequest();
EntraGuestAcctReq.setOperation(AccountRequest.Operation.Create);
EntraGuestAcctReq.setNativeIdentity(EmailAddress);
EntraGuestAcctReq.setApplication("Test Tenant Guest User");
acctReqs.add(EntraGuestAcctReq);
EntraGuestAcctReq.add(new AttributeRequest("invitedUserEmailAddress", ProvisioningPlan.Operation.Set, EmailAddress));
EntraGuestAcctReq.add(new AttributeRequest("inviteRedirectUrl", ProvisioningPlan.Operation.Set, RedirectURL));
EntraGuestAcctReq.add(new AttributeRequest("invitedUserDisplayName", ProvisioningPlan.Operation.Set, DisplayName));
EntraGuestAcctReq.add(new AttributeRequest("invitedUserUsageLocation", ProvisioningPlan.Operation.Set, UsageLocation));
EntraGuestAcctReq.add(new AttributeRequest("customizedMessageBody", ProvisioningPlan.Operation.Set, CustomInvitationMessage));
acctReqs.add(EntraGuestAcctReq);


plan.setAccountRequests(acctReqs);

workflow.put("plan", plan);
System.out.println("Provisioning plan created with plan: " + plan.toXml());

return plan;
